ST.xhtml:
IF Enable THEN
	Busy := TRUE;
	//Initializing drive
	Inverter.Controlword.1 := TRUE;
	Inverter.Controlword.3 := TRUE;

	//Motion interfaces control based on block inputs
	setRegulatorON(bRegulatorON);
	setDriveStart(bDriveStart);

	//outputs update
	bRegulatorRealState := regulatorRealState;
	bDriveStartRealState := driveStartRealState;
	Status := bRegulatorRealState AND bDriveStartRealState;

	//Error hanlding
	IF errorStatus THEN
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Axis_Error;
	END_IF
	IF bRegulatorON THEN
		IF NOT Inverter.Statusword.0 THEN
			Error := TRUE;
			ErrorID := IMC_Error.IMC_Drive_Not_Read_For_Switch_ON;
		END_IF
		IF NOT Inverter.Statusword.2 THEN
			Error := TRUE;
			ErrorID := IMC_Error.IMC_Opertaion_Not_Enabled;
		END_IF
		IF NOT Inverter.Statusword.4 THEN
			Error := TRUE;
			ErrorID := IMC_Error.IMC_Voltage_Disabled;
		END_IF
	END_IF
ELSE
	Status := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	Inverter.Controlword.0 := i_bRegulatorON;
	setRegulatorON := regulatorRealState;
ELSE
	RETURN;
END_IF



{warning restore C0371}

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	Inverter.Controlword.2 := i_bDriveStart;
	setDriveStart := driveStartRealState;
ELSE
	RETURN;
END_IF



{warning restore C0371}

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	driveStartRealState := Inverter.Statusword.5;
ELSE
	RETURN;
END_IF



{warning restore C0371}

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	regulatorRealState := Inverter.Statusword.1;
ELSE
	RETURN;
END_IF



{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	errorStatus := Inverter.Statusword.3 OR Inverter.Statusword.7;
ELSE
	RETURN;
END_IF
{warning restore C0371}

IF Execute  THEN
	CASE cykl OF
		-1:
			Error := TRUE;
			ErrorID := IMC_Error.IMC_Inverter_No_Error;
			Busy := FALSE;
		0:
			Busy := TRUE;
			cykl := 10;
		10:
			IF NOT errorStatus THEN
				cykl := -1;
			ELSE
				cykl := 20;
			END_IF
		20:
			Inverter.Controlword.7 := TRUE;
			cykl := 30;
		30:
			IF NOT errorStatus THEN
				Inverter.Controlword.7 := FALSE;
				cykl := 40;
			END_IF
		40:
			Done := TRUE;
			Busy := FALSE;
	END_CASE
ELSE
	Done := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
	Inverter.Controlword.7 := FALSE;
	cykl := 0;
END_IF

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	errorStatus := Inverter.Statusword.3 OR Inverter.Statusword.7;
ELSE
	RETURN;
END_IF



{warning restore C0371}

IF Execute AND NOT Done THEN
	Busy := TRUE;
	Inverter.modeOfOperation := ModeSelection;
	IF ControllerMode = ModeSelection THEN
		Done := TRUE;
		Busy := FALSE;
	END_IF
ELSE
	Done := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	CASE Inverter.ModeOfOperationDisplay OF
		1: ControllerMode := IMC_ControllerMode.IMC_Position;
		2: ControllerMode := IMC_ControllerMode.IMC_Velocity;
		4: ControllerMode := IMC_ControllerMode.IMC_Torque;
		6: ControllerMode := IMC_ControllerMode.IMC_Home;
	END_CASE
ELSE
	RETURN;
END_IF
{warning restore C0371}

IF Execute AND NOT Done THEN
	Busy := TRUE;
	Inverter.velocityUnits := VelocityUnits;
	IF Inverter.velocityUnits = VelocityUnits THEN
		Busy := FALSE;
		Done := TRUE;
	END_IF
ELSE
	Done := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := FALSE;
END_IF

IF Enable THEN
	Position := (ActPosition/Inverter.encoderPulses)*Inverter.distancePerRevolution;
	Valid := TRUE;
	Busy := TRUE;
ELSE
	Valid := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	ActPosition := Inverter.ActPosition;
ELSE
	RETURN;
END_IF



{warning restore C0371}

IF Enable THEN
	Busy := TRUE;
	IF NOT SDORead.xExecute THEN
		SDORead.xExecute := TRUE;
		Valid := FALSE;
	END_IF
	IF SDORead.xDone THEN
		SDORead.xExecute := FALSE;
		Valid := TRUE;
	END_IF
	IF SDORead.xError THEN
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Could_Not_Read_SDO;
	END_IF
	Torque := TO_LREAL(SDOData)/10;
ELSE
	Valid := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF

SDORead(
	usiCom:= 1, 
	uiDevice:= Inverter.ETCSlave, 
	usiChannel:= 1, 
	wIndex:= 16#6077, 
	bySubindex:= 16#0, 
	udiTimeOut:= 100, 
	pBuffer:= ADR(SDOData), 
	szSize:= SIZEOF(SDOData)
);

IF Enable THEN

	IF Inverter.velocityUnits = IMC_VelocityUnits.IMC_RPM THEN
		Velocity := ActVelocity;
	ELSIF Inverter.velocityUnits = IMC_VelocityUnits.IMC_PUU_PER_SEC THEN
		Velocity := ActVelocity * Inverter.encoderPulses;
	ELSIF Inverter.velocityUnits = IMC_VelocityUnits.IMC_MM_PER_MIN THEN
		Velocity := (ActVelocity * Inverter.distancePerRevolution);
	END_IF;

	Valid := TRUE;
	Busy := TRUE;
ELSE
	Valid := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	ActVelocity := Inverter.ActVelocity;
ELSE
	RETURN;
END_IF
{warning restore C0371}

IF Execute THEN
	CommandAborted := FALSE;
	OldExecute := TRUE;
	CASE i OF
	-3: 
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Axis_Wrong_ControllerMode;	
	-2: 
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Axis_Not_Startup;	
	-1:
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Axis_Error;
	0: 
		Busy := TRUE;
		i := 10;
	10:
		IF ErrorStatus THEN
			i := -1;
		ELSE
			i := 20;
		END_IF;
	20:
		IF NOT RegulatorRealState THEN
			i := -2;
		ELSE
			i := 30;
		END_IF;
	30:
		IF ControllerMode <> IMC_ControllerMode.IMC_Velocity THEN
			i := -3;
		ELSE
			i := 40;
		END_IF;
	40:
		IF Direction = IMC_Direction.IMC_Positive THEN
			Inverter.TargetVelocity := SwitchVelocityUnits(Velocity);
		ELSIF Direction = IMC_Direction.IMC_Negative THEN
			Inverter.TargetVelocity := -SwitchVelocityUnits(Velocity);
		END_IF;
		i := 50;
	50:
		Inverter.Controlword.4 := TRUE;
		Inverter.Controlword.5 := TRUE;
		Inverter.Controlword.6 := TRUE;
	END_CASE
ELSE
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
	i := 0;
END_IF;

IF OldExecute THEN
	Busy := TRUE;
	Active := TRUE;
	IF Inverter.Statusword.10 THEN
		InVelocity := TRUE;
	ELSE
		InVelocity := FALSE;
	END_IF;
ELSE
	Busy := FALSE;
	Active := FALSE;
	InVelocity := FALSE;
END_IF;

IF NOT Inverter.Controlword.4 OR NOT Inverter.Controlword.5 OR NOT Inverter.Controlword.6 OR ErrorStatus OR NOT RegulatorRealState THEN
	OldExecute := FALSE;
	CommandAborted := TRUE;
END_IF;

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	IF Inverter.VelocityUnits = IMC_VelocityUnits.IMC_RPM THEN
		SwitchVelocityUnits := TO_INT(CommandVelocity);
	ELSIF Inverter.VelocityUnits = IMC_VelocityUnits.IMC_MM_PER_MIN THEN
		SwitchVelocityUnits := TO_INT(CommandVelocity/Inverter.DistancePerRevolution);
	ELSIF Inverter.VelocityUnits = IMC_VelocityUnits.IMC_PUU_PER_SEC THEN
		SwitchVelocityUnits := TO_INT((CommandVelocity/Inverter.DistancePerRevolution)*60);
	END_IF;
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	errorStatus := Inverter.Statusword.3 OR Inverter.Statusword.7;
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	CASE Inverter.ModeOfOperationDisplay OF
		1: ControllerMode := IMC_ControllerMode.IMC_Position;
		2: ControllerMode := IMC_ControllerMode.IMC_Velocity;
		4: ControllerMode := IMC_ControllerMode.IMC_Torque;
		6: ControllerMode := IMC_ControllerMode.IMC_Home;
	END_CASE
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	RegulatorRealState := Inverter.Statusword.1;
ELSE
	RETURN;
END_IF
{warning restore C0371}

IF Enable THEN
	Busy := TRUE;
	IF NOT SDORead.xExecute THEN
		SDORead.xExecute := TRUE;
		Valid := FALSE;
		ErrorID := IMC_Error.IMC_No_Error;
	END_IF
	IF SDORead.xDone THEN
		SDORead.xExecute := FALSE;
		Valid := TRUE;
		ptr := ADR(SDOData);
		AxisErrorID := ptr^;
		ptr := ptr + SIZEOF(BYTE);
		AxisWarningID := ptr^;
	END_IF
	IF SDORead.xError THEN
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Could_Not_Read_SDO;
		SDORead.xExecute := FALSE;		
	END_IF
ELSE
	Valid := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF

SDORead(
	usiCom:= 1, 
	uiDevice:= 1002, 
	usiChannel:= 1, 
	wIndex:= 16#3021, 
	bySubindex:= 16#1, 
	udiTimeOut:= 100,
	pBuffer:= ADR(SDOData), 
	szSize:= SIZEOF(SDOData)
);

SDOErrorID := SDORead.eError;

IF Enable THEN
	Busy := TRUE;
	//Errorstop status
	IF errorStatus THEN
		Errorstop := TRUE;
	ELSE
		Errorstop := FALSE;
	END_IF;
	//Disabled status
	IF NOT errorStatus AND NOT RegulatorRealState THEN
		Disabled := TRUE;
	ELSE
		Disabled := FALSE;
	END_IF;
	//Standstill status
	IF NOT Errorstop AND RegulatorRealState AND ActVelocity = 0 THEN
		StandStill := TRUE;
	ELSE
		StandStill := FALSE;
	END_IF;
	//Stopping status
// 	IF NOT Errorstop AND RegulatorRealState AND ActVelocity <> 0 AND Inverter.Controlword <= 16#0F THEN
// 		Stopping := TRUE;
// 	ELSE
// 		Stopping := FALSE;
// 	END_IF;
	//Discrete motion
	IF NOT Errorstop AND RegulatorRealState AND ActVelocity <> 0 AND ControllerMode = IMC_ControllerMode.IMC_Position THEN
		DiscreteMotion := TRUE;
	ELSE
		DiscreteMotion := FALSE;
	END_IF;
	//Continuous motion
	IF NOT Errorstop AND RegulatorRealState AND ActVelocity <> 0 AND ControllerMode = IMC_ControllerMode.IMC_Velocity THEN
		ContinuousMotion := TRUE;
	ELSE
		ContinuousMotion := FALSE;
	END_IF;
	//Homing status
	IF NOT Errorstop AND RegulatorRealState AND ActVelocity <> 0 AND ControllerMode = IMC_ControllerMode.IMC_Home THEN
		Homing := TRUE;
	ELSE
		Homing := FALSE;
	END_IF;	
	//ConstantVelocity
	IF NOT Errorstop AND RegulatorRealState AND MotionStatus = IMC_MotionStatus.IMC_ConstantVelocity THEN
		ConstantVelocity := TRUE;
	ELSE
		ConstantVelocity := FALSE;
	END_IF;
	//Accelerating
	IF NOT Errorstop AND RegulatorRealState AND MotionStatus = IMC_MotionStatus.IMC_Accelerating THEN
		Accelerating := TRUE;
	ELSE
		Accelerating := FALSE;
	END_IF;
	//Decelerating
	IF NOT Errorstop AND RegulatorRealState AND MotionStatus = IMC_MotionStatus.IMC_Decelerating THEN
		Decelerating := TRUE;
	ELSE
		Decelerating := FALSE;
	END_IF;
	Valid := TRUE;
ELSE
	Valid := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
	Disabled := FALSE;
	Errorstop := FALSE;
	//Stopping := FALSE;
	StandStill := FALSE;
	DiscreteMotion := FALSE;
	ContinuousMotion := FALSE;
	//SynchronizedMotion := FALSE;
	Homing := FALSE;
	ConstantVelocity := FALSE;
	Accelerating := FALSE;
	Decelerating := FALSE;
END_IF

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	errorStatus := Inverter.Statusword.3 OR Inverter.Statusword.7;
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	ActVelocity := Inverter.ActVelocity;
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}

IF __ISVALIDREF (Inverter) THEN
	RegulatorRealState := Inverter.Statusword.1;
ELSE
	RETURN;
END_IF

{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	CASE Inverter.ModeOfOperationDisplay OF
		1: ControllerMode := IMC_ControllerMode.IMC_Position;
		2: ControllerMode := IMC_ControllerMode.IMC_Velocity;
		4: ControllerMode := IMC_ControllerMode.IMC_Torque;
		6: ControllerMode := IMC_ControllerMode.IMC_Home;
	END_CASE
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	IF (ActVelocity > 0 AND ActVelocity > PrevVelocity) OR (ActVelocity < 0 AND ActVelocity < PrevVelocity) THEN
		MotionStatus := IMC_MotionStatus.IMC_Accelerating;
	ELSIF (ActVelocity > 0 AND ActVelocity < PrevVelocity) OR (ActVelocity < 0 AND ActVelocity > PrevVelocity) THEN
		MotionStatus := IMC_MotionStatus.IMC_Decelerating;
	ELSIF ActVelocity = PrevVelocity AND ActVelocity <> 0 THEN
		MotionStatus := IMC_MotionStatus.IMC_ConstantVelocity;
	ELSIF ActVelocity = 0 THEN
		MotionStatus := IMC_MotionStatus.IMC_Standstill;
	END_IF;
	PrevVelocity := ActVelocity;
ELSE
	RETURN;
END_IF
{warning restore C0371}

IF Execute THEN
	Busy := TRUE;
	IF MotionStatus = IMC_MotionStatus.IMC_Standstill AND NOT Done THEN
		Error := TRUE;
		ErrorID := IMC_Error.IMC_Axis_Not_In_Motion;
	ELSE
		IF ControllerMode = IMC_ControllerMode.IMC_Position OR ControllerMode = IMC_ControllerMode.IMC_Home THEN
			Inverter.Controlword.8 := TRUE;
		ELSIF ControllerMode = IMC_ControllerMode.IMC_Velocity THEN
			Inverter.Controlword.4 := FALSE;
			Inverter.Controlword.5 := FALSE;
			Inverter.Controlword.6 := FALSE;
		END_IF;
		IF Inverter.ActVelocity = 0 THEN
			Done := TRUE;
		END_IF;
	END_IF;
ELSE
	Done := FALSE;
	Busy := FALSE;
	Error := FALSE;
	ErrorID := IMC_Error.IMC_No_Error;
END_IF;

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	CASE Inverter.ModeOfOperationDisplay OF
		1: ControllerMode := IMC_ControllerMode.IMC_Position;
		2: ControllerMode := IMC_ControllerMode.IMC_Velocity;
		4: ControllerMode := IMC_ControllerMode.IMC_Torque;
		6: ControllerMode := IMC_ControllerMode.IMC_Home;
	END_CASE
ELSE
	RETURN;
END_IF
{warning restore C0371}

{warning disable C0371}
IF __ISVALIDREF (Inverter) THEN
	IF Inverter.ActVelocity > 0 AND Inverter.ActVelocity > PrevVelocity OR Inverter.ActVelocity < 0 AND Inverter.ActVelocity < PrevVelocity THEN
		MotionStatus := IMC_MotionStatus.IMC_Accelerating;
	ELSIF Inverter.ActVelocity > 0 AND Inverter.ActVelocity < PrevVelocity OR Inverter.ActVelocity < 0 AND Inverter.ActVelocity > PrevVelocity THEN
		MotionStatus := IMC_MotionStatus.IMC_Decelerating;
	ELSIF Inverter.ActVelocity = PrevVelocity THEN
		MotionStatus := IMC_MotionStatus.IMC_ConstantVelocity;
	ELSIF Inverter.ActVelocity = 0 THEN
		MotionStatus := IMC_MotionStatus.IMC_Standstill;
	END_IF;
	PrevVelocity := Inverter.ActVelocity;
ELSE
	RETURN;
END_IF
{warning restore C0371}
